<?php
/**
 * Template part for displaying a property card
 */
?>
<a href="<?php the_permalink(); ?>" class="property-link">
    <div class="property-card">
        <?php
        // --- Property Banner from Property Hero layout ---
        if (have_rows('hero_content')) :
            while (have_rows('hero_content')) : the_row();
                if (get_row_layout() === 'property_hero') :
                    $featured_banner = get_sub_field('property_banner'); // Image field
                    if ($featured_banner && isset($featured_banner['url'])) :
        ?>
                        <div class="property-image">
                            <img src="<?php echo esc_url($featured_banner['url']); ?>"
                                 alt="<?php echo esc_attr($featured_banner['alt'] ?? get_the_title()); ?>" />
                        </div>
        <?php
                    endif;
                endif;
            endwhile;
        endif;
        ?>

        <div class="property-info">

            <h2 class="property-title"><?php the_title(); ?></h2>

            <div class="property-raw-detail">
                 <?php
                    // Location (Meta Field)
                    $location = get_post_meta(get_the_ID(), '_property_location', true);
                    if ($location) :
                ?>
                    <div class="property-location row-flex">
                        <svg width="20" height="20" viewBox="0 0 20 20" fill="none" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink">
                            <rect width="20" height="20" fill="url(#pattern0_47_631)"/>
                            <defs>
                            <pattern id="pattern0_47_631" patternContentUnits="objectBoundingBox" width="1" height="1">
                            <use xlink:href="#image0_47_631" transform="scale(0.00195312)"/>
                            </pattern>
                            <image id="image0_47_631" width="512" height="512" preserveAspectRatio="none" xlink:href="data:image/png;base64,"/>
                            </defs>
                        </svg>

                        <?php echo esc_html($location); ?>
                    </div>
                <?php endif; ?>

                <?php
                // --- Get Price Range from Property Detail layout ---
                $price_range = '';
                if (have_rows('hero_content')) :
                    while (have_rows('hero_content')) : the_row();
                        if (get_row_layout() === 'property_detail') :
                            $detail = get_sub_field('detail'); // Group
                            if (!empty($detail)) :
                                $price_range = $detail['price_range'] ?? '';
                            endif;
                        endif;
                    endwhile;
                endif;

                // Display Price Range
                if (!empty($price_range)) :
                ?>
                    <div class="property-price row-flex">
                        <svg width="20" height="20" viewBox="0 0 20 20" fill="none" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink">
                            <rect width="20" height="20" fill="url(#pattern0_47_630)"/>
                            <defs>
                            <pattern id="pattern0_47_630" patternContentUnits="objectBoundingBox" width="1" height="1">
                            <use xlink:href="#image0_47_630" transform="scale(0.00195312)"/>
                            </pattern>
                            <image id="image0_47_630" width="512" height="512" preserveAspectRatio="none" xlink:href="data:image/png;base64,"/>
                            </defs>
                        </svg>

                        <?php echo esc_html($price_range); ?>
                    </div>
                <?php endif; ?>

                <?php
                // --- Property Status (ACF field) ---
                $status = get_field('property_status');
                if ($status) :
                ?>
                    <div class="property-status row-flex">
                        <svg width="20" height="20" viewBox="0 0 20 20" fill="none" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink">
                            <rect width="20" height="20" fill="url(#pattern0_47_629)"/>
                            <defs>
                            <pattern id="pattern0_47_629" patternContentUnits="objectBoundingBox" width="1" height="1">
                            <use xlink:href="#image0_47_629" transform="scale(0.00195312)"/>
                            </pattern>
                            <image id="image0_47_629" width="512" height="512" preserveAspectRatio="none" xlink:href="data:image/png;base64,"/>
                            </defs>
                        </svg>

                        <?php echo esc_html($status); ?>
                    </div>
                <?php endif; ?>
            </div>
            <div class="floating-arrow-right-l">
                <svg width="24" height="24" viewBox="0 0 16 16" fill="none" xmlns="http://www.w3.org/2000/svg">
                <path d="M2.16675 13.8333L13.8334 2.16663M13.8334 2.16663H2.16675M13.8334 2.16663V13.8333" 
                        stroke="#F9FAF3" 
                        stroke-width="2.5" 
                        stroke-linecap="round" 
                        stroke-linejoin="round"/>
                </svg>
            </div>
        </div>
    </div>
</a>