INSERT INTO `wp_postmeta` VALUES (1019,194,'page_content_0_hero_description','SMDC Turf Residences nestled at the heart of the thriving southern metropolis lies a hidden gem waiting to be discovered. With its tagline \"My Home My Turf,\" this exclusive property offers a unique blend of urban sophistication and natural tranquility, setting the stage for a lifestyle unlike any other.');
