INSERT INTO `wp_postmeta` VALUES (1027,194,'page_content_1_closing_text','<p style=\"margin: 0cm; text-align: justify; line-height: 17.55pt;\"><span style=\"font-size: 13.5pt; font-family: \'Arial\',sans-serif; color: #414141; letter-spacing: .1pt;\">SMDC Turf Residences is positioned strategically between the bustling SLEX-CALAX interchange and the dynamic SM City Santa Rosa lifestyle district, Turf Residences provides an unparalleled fusion of city excitement and serene landscapes. Experience the harmonious blend of urban pulse and natural tranquility, where the best of both worlds converge seamlessly.</span></p>');
