INSERT INTO `wp_postmeta` VALUES (1089,194,'page_content_3_split_text','Bloom Residences features mid-rise living with efficient layouts and balconies in select unitsspaces sized for real life, from starting out to starting a family. With all units being two-bedroom units, natural light, ventilation, and proximity to everyday needs anchor the residential experience at Bloom, while on-site property management and 24/7 security provide the reliability residents expect from an SMDC community.');
